import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { FlatList, View } from 'react-native';
import ViewProductOrder from '../components/Products/ViewProductOrder';

const currentLocation = "http://192.168.1.103:3000/api";

export default function OrderDetails({ route }) {
  const { id } = route.params;
  console.log(`${currentLocation}/orders/${id}`);
  const [total, setTotal] = useState();
  const [order, setOrder] = useState();

  function getTotal(products) {
    let sum = 0;
    for (const i in products) {
      sum += products[i].quantity * products[i].price;
    }
    setTotal(sum);
  };

  function fetchData() {
    axios.get(`${currentLocation}/orders/${id}`)
      .then(res => {
        console.log(res.data);
        console.log(`${currentLocation}/orders/${id}`);
        setOrder(res.data);
        getTotal(res.data.products);
      })
      .catch(error => console.log(error));
  }

  useEffect(() => {
    fetchData();
  }, []);

  return (
    <View>
      {console.log(order)}
      {order == "undefined" ? (
        <Text>error</Text>
      ) : (
        <View>
          {/* <View>
            <Text>#{order.id}</Text>
          </View>
          <View>
            <Text>Vận chuyển</Text>
            <Text>{order.status}</Text>
          </View>
          <View>
            <Text>Sản phẩm</Text>
            <FlatList
              data={order.products}
              renderItem={({ item }) =>
                <ViewProductOrder product={item} />
              }
              keyExtractor={item => `${item.id}`}
            />
          </View>
          <View>
            <Text>Thành tiền:</Text>
            <Text>{total}</Text>
          </View> */}
        </View>
      )}
    </View>
  );
}
