import React, { useEffect, useState } from 'react';
import axios from "axios";
import { View, Text, TouchableOpacity, FlatList, Button, Linking } from "react-native";
import ViewProductOrder from "../../components/Products/ViewProductOrder";
import RadioForm, { RadioButton, RadioButtonInput, RadioButtonLabel } from 'react-native-simple-radio-button';

const currentLocation = "http://192.168.1.103:3000/api";

export default function Payment({ route }) {
  const { cart } = route.params;
  console.log(cart);


  function handleClick() {
    Linking.openURL('http://192.168.1.103:5000?');
  };

  const [radioProps, setRadioProps] = useState([
    { label: 'param1', value: 0 },
    { label: 'param2', value: 1 }
  ]);


  return (
    <View>
      <View>
        <Text>Sản phẩm</Text>
        <FlatList
          data={cart}
          renderItem={({ item }) =>
            <ViewProductOrder product={item} />
          }
          keyExtractor={item => `${item.id}`}
        />
      </View>
      <RadioForm
        radio_props={radio_props}
        initial={0}
        onPress={(value) => { this.setState({ value: value }) }}
      />
    </View>
  );
}
