import React, { useEffect, useState } from 'react';
import axios from "axios";
import { View, Text, TouchableOpacity, TextInput, Linking, StyleSheet, Button } from "react-native";
import ViewProductOrder from "../../components/Products/ViewProductOrder";

const currentLocation = "http://192.168.1.103:3000/api";

export default function Payment({ route }) {
  // const { cart, method } = route.params;
  const [name, onChangeName] = React.useState(null);
  const [number, onChangeNumber] = React.useState(null);
  const [address, onChangeAddress] = React.useState(null);
  const [select, setSelect] = useState("paypal");

  return (
    <View style={styles.container}>
      <View>
        <Text>Họ và tên</Text>
        <TextInput
          style={styles.input}
          onChangeText={onChangeName}
          value={name}
        />
        <Text>Số điện thoại</Text>
        <TextInput
          style={styles.input}
          onChangeText={onChangeNumber}
          value={number}
          keyboardType="numeric"
        />
        <Text>Địa chỉ</Text>
        <TextInput
          style={styles.input}
          onChangeText={onChangeAddress}
          value={address}
        />
      </View>
      <View style={styles.footer}>
        <TouchableOpacity
          activeOpacity={1}
        >
          <View style={styles.order}>
            <Text style={{ textAlign: 'center', fontWeight: 'bold' }}>ĐẶT HÀNG</Text>
          </View>
        </TouchableOpacity>
      </View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: 8,
    paddingHorizontal: 8,
    paddingBottom: 32
  },
  content: {
    marginBottom: 8,
    padding: 8
  },
  button: {
    borderRadius: 5,
    elevation: 3,
    padding: 16,
    marginBottom: 16,
    backgroundColor: '#fff',
  },
  default: {
    borderColor: '#ccc',
    borderWidth: 0.5
  },
  select: {
    borderColor: '#ff9999',
    backgroundColor: '#ff9999',
  },
  title: {
    color: '#ff9999',
    fontWeight: 'bold',
    fontSize: 18,
    marginBottom: 8
  },
  footer: {
    position: 'absolute',
    bottom: 0,
    left: 0,
    right: 0,
  },
  order: {
    backgroundColor: "#ff4d4d",
    paddingVertical: 16,
  },
  input: {
    height: 40,
    margin: 12,
    borderWidth: 1,
    padding: 10,
  }
});
