import React, { useState, useEffect } from 'react';
import { Text, View, StyleSheet } from 'react-native';
import ViewProductOrder from '../Products/ViewProductOrder';

function OrderListItem(props) {
  const { order } = props;
  const [total, setTotal] = useState(0);
  const [product, setProduct] = useState({});
  const [numProducts, setNumProducts] = useState(0);

  function getTotal(products) {
    let sum = 0;
    for (const i in products) {
      sum += products[i].quantity * products[i].price;
    }
    setTotal(sum);
  };

  useEffect(() => {
    getTotal(order.products);
    setProduct(order.products[0]);
    setNumProducts(order.products.length);
  });

  return (
    <View style={styles.container}>
      <View style={styles.row}>
        <Text>{order.id}</Text>
        <Text>{order.status}</Text>
      </View>
      {/* <View style={styles.row}>
        <ViewProductOrder product={product} />
      </View> */}
      <View>
        <Text>{order.products.lenght} sản phẩm</Text>
        <Text>Thành tiền: {total}</Text>
      </View>
    </View>
  );
}

export default OrderListItem;

const boxSize = 80;

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    marginBottom: 16,
    paddingHorizontal: 16,
    paddingVertical: 8,
    flexDirection: 'row',
    alignItems: 'center',
    elevation: 2,
    borderColor: '#ccc',
    borderWidth: 0.5
  },
  img: {
    width: boxSize,
    height: boxSize,
    marginRight: 4
  },
  row: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  col: {
    flexDirection: 'column'
  },
  col2: {
    width: '50%'
  },
  title: {
    fontSize: 20,
    fontWeight: 'bold'
  },
  subText: {
    fontSize: 18,
  },
  textRight: {
    textAlign: 'right'
  },
  quantity: {
    fontSize: 20,
    fontWeight: '700',
    marginHorizontal: 4
  },
  mrAuto: {
    marginRight: 'auto'
  }
});
