import React from 'react';
import axios from 'axios';
import { Image, Text, View, StyleSheet, TouchableOpacity } from 'react-native'
import ProductImg from '../../assets/product.png'

function ViewProductOrder(props) {
  const { product } = props;
  return (
    <View style={styles.container}>
      <Image style={styles.productImg} source={ProductImg} />
      <View>
        <Text style={styles.title}>{product.id}</Text>
        <View style={styles.row}>
          <Text style={styles.subText}>{product.type}, {product.type}: x{product.quantity}</Text>
          <Text style={styles.subText}>{product.price * product.quantity}</Text>
        </View>
      </View>
    </View>
  );
}

export default ViewProductOrder;

const styles = StyleSheet.create({
  container: {
    padding: 4,
    borderRadius: 4,
    backgroundColor: '#FFF',
    elevation: 5,
    marginBottom: 8,
    flexDirection: 'column'
  },
  productImg: {
    height: 50,
    width: 50
  },
  productTitle: {
    fontSize: 16,
    fontWeight: 'bold',
    marginVertical: 4
  },
  row: {
    flexDirection: 'row',
    alignItems: 'center'
  },
  subText: {
    fontSize: 13,
    marginRight: 'auto'
  },
  price: {
    color: 'blue',
    fontWeight: 'bold',
    fontSize: 13,
    textAlign: 'right',

  }
});
