import React, { useState, useEffect } from "react";
import { Image, View, Text, Button, TouchableOpacity, StyleSheet } from "react-native";
import ImgAva from "../../assets/SR.TKNK.png";
import axios from "axios";

const currentLocation = "http://192.168.1.103:3000/api";

export default function AccountScreen() {
  const username = "ndt123";
  const [infor, setInfor] = useState({});

  async function fetchData() {
    await axios.get(`${currentLocation}/users?username=${username}`)
      .then(res => {
        const infor = res.data;
        setInfor(infor);
      })
      .catch(error => console.log(error));
  }

  useEffect(() => {
    fetchData();
  });

  return (
    <View style={styles.container}>
      {console.log(infor)}
      <View style={styles.header}>
        <Image style={styles.image} source={ImgAva} />
        <View>
          <Text style={styles.titleName}>{infor.name}</Text>
          <Button color="#ff8080" title="Cập nhật hồ sơ" />
        </View>
      </View>
      <View style={styles.content}>
        <Text style={styles.title}>Ví của tôi: {infor.amount} VND</Text>
      </View>
      <TouchableOpacity style={styles.content}>
        <Text style={styles.title}>Hỗ trợ</Text>
      </TouchableOpacity>
      <TouchableOpacity >
        <View style={styles.content}>
          <Text style={styles.title}>Cài đặt</Text>
        </View>
      </TouchableOpacity>
      <TouchableOpacity style={styles.content}>
        <Text style={styles.title}>Đăng xuất</Text>
      </TouchableOpacity>
    </View>
  );
}

const boxsize = 80;

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header: {
    paddingTop: 60,
    paddingBottom: 20,
    backgroundColor: '#ffb3b3',
    flexDirection: 'row',
    paddingHorizontal: 16,
    alignItems: 'center',
  },
  image: {
    width: boxsize,
    height: boxsize,
    borderRadius: boxsize / 2,
    borderColor: '#fff',
    borderWidth: 0.5,
    marginRight: 16
  },
  titleName: {
    fontWeight: 'bold',
    fontSize: 25,
    marginBottom: 8
  },
  content: {
    alignItems: 'stretch',
    borderBottomColor: '#ccc',
    borderBottomWidth: 0.5,
    paddingHorizontal: 20,
    paddingVertical: 16,
    backgroundColor: '#fff'
  },
  title: {
    fontSize: 20
  }
});
