import React, { useState, useEffect, useCallback } from "react";
import { Image, View, Text, Button, TouchableOpacity, StyleSheet, ActivityIndicator } from "react-native";
import Account from "../../components/Account";
import axios from "axios";

const currentLocation = "http://192.168.1.103:3000/api";



export default function AccountScreen() {
  const username = "ndt123";
  const [isLoading, setIsLoading] = useState(false);
  const [user, setUser] = useState({});

  useEffect(() => {
    axios.get(`${currentLocation}/users?username=${username}`)
      .then(res => {
        setIsLoading(true);
        setUser(res.data);
        console.log(user);
        setIsLoading(false);
      })
      .catch(error => console.log(error));
  }, [])

  return (
    <View>
      {console.log(isLoading, user)}
      {isLoading == true ? (
        <View style={{ flex: 1, justifyContent: "center", alignItems: "center" }}>
          <ActivityIndicator size="large" />
        </View>
      ) : (
        <View>
          {console.log(isLoading, user)}
          < Account user={user} />
        </View>
      )}
    </View>
  );
}

const boxsize = 80;

const styles = StyleSheet.create({
  container: {
    flex: 1
  },
  header: {
    paddingTop: 60,
    paddingBottom: 20,
    backgroundColor: '#ffb3b3',
    flexDirection: 'row',
    paddingHorizontal: 16,
    alignItems: 'center',
  },
  image: {
    width: boxsize,
    height: boxsize,
    borderRadius: boxsize / 2,
    borderColor: '#fff',
    borderWidth: 0.5,
    marginRight: 16
  },
  titleName: {
    fontWeight: 'bold',
    fontSize: 25,
    marginBottom: 8
  },
  content: {
    alignItems: 'stretch',
    borderBottomColor: '#ccc',
    borderBottomWidth: 0.5,
    paddingHorizontal: 20,
    paddingVertical: 16,
    backgroundColor: '#fff'
  },
  title: {
    fontSize: 20
  }
});
