import React, { useEffect, useState } from 'react';
import axios from "axios";
import { View, Text, TouchableOpacity, FlatList, ScrollView, Linking, StyleSheet } from "react-native";
import ViewProductOrder from "../../components/Products/ViewProductOrder";

const currentLocation = "http://192.168.1.103:3000/api";

export default function Payment({ route }) {
  const { cart } = route.params;
  const [select, setSelect] = useState("paypal");

  function handleChoose(method) {
    setSelect(method);
  }

  function handleClick() {
    Linking.openURL('http://192.168.1.103:5000?');
  };


  return (
    <ScrollView style={styles.container}>
      <View style={styles.content}>
        <Text style={styles.title}>Sản phẩm</Text>
        <FlatList
          data={cart}
          renderItem={({ item }) =>
            <ViewProductOrder product={item} />
          }
          keyExtractor={item => `${item.id}`}
        />
      </View>
      <View style={styles.content}>
        <Text style={styles.title}>Chọn phương thức thanh toán</Text>
        <TouchableOpacity
          activeOpacity={1}
          onPress={() => handleChoose("paypal")}
        >
          <View style={[styles.button, select !== "paypal" ? styles.default : styles.select]}>
            <Text>Thanh toán bằng Paypal</Text>
          </View>
        </TouchableOpacity>
        <TouchableOpacity
          activeOpacity={1}
          onPress={() => handleChoose("cash")}
        >
          <View style={[styles.button, select !== "cash" ? styles.default : styles.select]}>
            <Text>Thanh toán khi nhận hàng</Text>
          </View>
        </TouchableOpacity>

        <TouchableOpacity
          activeOpacity={1}
          onPress={() => handleChoose("qr")}
        >
          <View style={[styles.button, select !== "qr" ? styles.default : styles.select]}>
            <Text>Thanh toán bằng mã QR</Text>
          </View>
        </TouchableOpacity>
      </View>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    paddingTop: 16,
    paddingHorizontal: 16
  },
  content: {
    marginBottom: 8,
  },
  button: {
    borderRadius: 5,
    elevation: 5,
    padding: 16,
    marginBottom: 16,
    backgroundColor: '#fff',
  },
  default: {
    borderColor: '#ccc',
    borderWidth: 0.5
  },
  select: {
    borderColor: '#ff9999',
    backgroundColor: '#ff9999',
  },
  title: {
    color: '#ff9999',
    fontWeight: 'bold',
    fontSize: 18,
    marginBottom: 8
  }
});
