import axios from 'axios';
import React, { useEffect, useState } from 'react';
import { FlatList, View } from 'react-native';
import { Header } from '../components/Header';
import ViewProductOrder from '../components/Products/ViewProductOrder';

const currentLocation = "http://192.168.1.103:3000/api";

function OrderDetails({ route }) {
  const { id } = route.params;
  const { total, setTotal } = useState();
  const { order, setOrder } = useState();

  function getTotal(products) {
    let sum = 0;
    for (const i in products) {
      sum += products[i].quantity * products[i].price;
    }
    setTotal(sum);
  };

  function fetchData(id) {
    axios.get(`${currentLocation}/orders/${id}`)
      .then(res => {
        const order = res.data;
        setOrder(order);
        console.log(order);
        getTotal(order.products);
      })
      .catch(error => console.log(error));
  }

  useEffect(() => {
    fetchData(id);
  });

  return (
    <View>
      <View>
        <Text>#{id}</Text>
      </View>
      <View>
        <Text>Vận chuyển</Text>
        <Text>{order.status}</Text>
      </View>
      <View>
        <Text>Sản phẩm</Text>
        <FlatList
          data={order.products}
          renderItem={({ item }) =>
            <ViewProductOrder product={item} />
          }
          keyExtractor={item => `${item.id}`}
        />
      </View>
      <View>
        <Text>Thành tiền:</Text>
        <Text>{total}</Text>
      </View>
    </View>
  );
}

export default OrderDetails;
