import React from 'react';
import {
  Image,
  Text,
  View,
  StyleSheet,
  TouchableOpacity,
} from 'react-native'

function CategoryListItem(props) {
  const { category, onPress } = props;
  return (
    <TouchableOpacity
      activeOpacity={0.5}
      onPress={onPress}
    >
      <View style={styles.container}>
        <Text style={styles.categoryTitle} >{category.name}</Text>
        <Image style={styles.categoryImg} source={{ uri: category.image }} />
      </View>
    </TouchableOpacity>
  );
}

export default CategoryListItem;

const styles = StyleSheet.create({
  container: {
    alignItems: 'center',
    padding: 16,
    borderRadius: 4,
    backgroundColor: '#FFF',
    marginBottom: 16,
    borderColor: '#000',
    marginLeft: 4,
    marginRight: 4
  },
  categoryImg: {
    width: 35,
    height: 35
  },
  categoryTitle: {
    fontSize: 8,
    fontWeight: 'bold',
    textTransform: 'uppercase',
    marginBottom: 8,
  },
});
